import math

cypher = ""

columnCount = 4
columnLength = math.ceil(len(cypher) / columnCount)
columns = [cypher[i:i+columnLength] for i in range(0, len(cypher), columnLength)]
plaintext = ""

for i in range(columnLength): 
    for k in range(columnCount):
        plaintext += columns[k][i]

print(plaintext[:30])
